      out.write("<script>\r\n");
      out.write("\t$('#createClueBtn').click(function () {\r\n");
      out.write("\t\t//异步查询所有者信息\r\n");
      out.write("\t\t//打开新建市场活动模态窗查询所有用户\r\n");
      out.write("\t\t$.ajax({\r\n");
      out.write("\t\t\turl : '/crm/workbench/activity/queryAllUsers',\r\n");
      out.write("\t\t\ttype : 'get',\r\n");
      out.write("\t\t\tdataType : 'json',\r\n");
      out.write("\t\t\tsuccess : function(data){\r\n");
      out.write("\t\t\t\tfor(var i = 0 ; i < data.length;i++){\r\n");
      out.write("\t\t\t\t\t$('#create-clueOwner')\r\n");
      out.write("\t\t\t\t\t\t\t.append(\"<option value=\"+data[i].id+\">\"+data[i].name+\"</option>\");\r\n");
      out.write("\t\t\t\t}\r\n");
      out.write("\t\t\t}\r\n");
      out.write("\t\t});\r\n");
      out.write("\t\t//异步查询字典数据\r\n");
      out.write("\t\t$.ajax({\r\n");
      out.write("\t\t\turl : '/crm/dictionaryCache',\r\n");
      out.write("\t\t\ttype : 'get',\r\n");
      out.write("\t\t\tdataType : 'json',\r\n");
      out.write("\t\t\tsuccess : function(data){\r\n");
      out.write("\t\t\t\t//先遍历所有的字典类型\r\n");
      out.write("\t\t\t\tfor(var i = 0 ; i < data.length; i++){\r\n");
      out.write("\t\t\t\t\tvar dictionaryList = data[i].dictionaryValues;\r\n");
      out.write("\t\t\t\t\tfor(var j = 0; j < dictionaryList.length; j++){\r\n");
      out.write("\t\t\t\t\t\tvar dictionaryValue = dictionaryList[j];\r\n");
      out.write("\t\t\t\t\t\tif(\"appellation\" == data[i].code){\r\n");
      out.write("\t\t\t\t\t\t\t$('#create-call')\r\n");
      out.write("\t\t\t\t\t\t\t\t\t.append(\"<option value=\"+dictionaryValue.value+\">\"+dictionaryValue.text+\"</option>\");\r\n");
      out.write("\t\t\t\t\t\t}\r\n");
      out.write("\t\t\t\t\t\tif(\"clueState\" == data[i].code){\r\n");
      out.write("\t\t\t\t\t\t\t$('#create-status')\r\n");
      out.write("\t\t\t\t\t\t\t\t\t.append(\"<option value=\"+dictionaryValue.value+\">\"+dictionaryValue.text+\"</option>\");\r\n");
      out.write("\t\t\t\t\t\t}\r\n");
      out.write("\t\t\t\t\t\tif(\"source\" == data[i].code){\r\n");
      out.write("\t\t\t\t\t\t\t$('#create-source')\r\n");
      out.write("\t\t\t\t\t\t\t\t\t.append(\"<option value=\"+dictionaryValue.value+\">\"+dictionaryValue.text+\"</option>\");\r\n");
      out.write("\t\t\t\t\t\t}\r\n");
      out.write("\t\t\t\t\t}\r\n");
      out.write("\t\t\t\t}\r\n");
      out.write("\t\t\t}\r\n");
      out.write("\r\n");
      out.write("\t\t});\r\n");
      out.write("\r\n");
      out.write("\t});\r\n");
      out.write("\r\n");
      out.write("\t//创建线索模态窗口日历插件\r\n");
      out.write("\t$(\"#create-nextContactTime\").datetimepicker({\r\n");
      out.write("\t\tlanguage:  \"zh-CN\",\r\n");
      out.write("\t\tformat: \"yyyy-mm-dd\",//显示格式\r\n");
      out.write("\t\tminView: \"month\",//设置只显示到月份\r\n");
      out.write("\t\tinitialDate: new Date(),//初始化当前日期\r\n");
      out.write("\t\tautoclose: true,//选中自动关闭\r\n");
      out.write("\t\ttodayBtn: true, //显示今日按钮\r\n");
      out.write("\t\tclearBtn : true,\r\n");
      out.write("\t\tpickerPosition: \"bottom-left\"\r\n");
      out.write("\t});\r\n");
      out.write("\t//点击线索创建模态窗口的保存按钮异步提交线索数据进行保存 &..&..\r\n");
      out.write("\t//{\"\":\"\",\"\":\",}\r\n");
      out.write("\t$('#saveClueBtn').click(function () {\r\n");
      out.write("\t\tvar saveClueForm = $('#saveClueForm').serialize();\r\n");
      out.write("\t\t$.ajax({\r\n");
      out.write("\t\t\turl : '/crm/workbench/clue/saveClue',\r\n");
      out.write("\t\t\tdata : saveClueForm,\r\n");
      out.write("\t\t\ttype : 'post',\r\n");
      out.write("\t\t\tdataType : 'json',\r\n");
      out.write("\t\t\tsuccess : function(data){\r\n");
      out.write("\t\t\t\talert(data.mess);\r\n");
      out.write("\t\t\t\t//关闭模态窗\r\n");
      out.write("\t\t\t\t$('#createClueModal').modal('hide');\r\n");
      out.write("\t\t\t}\r\n");
      out.write("\t\t});\r\n");
      out.write("\t});\r\n");
      out.write("\r\n");
      out.write("\r\n");
      out.write("\r\n");
      out.write("\t//点击查询按钮查询数据\r\n");
      out.write("\tfunction queryActivity(){\r\n");
      out.write("\t\tpageList(1,2);\r\n");
      out.write("\t}\r\n");
      out.write("\r\n");
      out.write("\t/*参数一:当前页码\r\n");
      out.write("\t参数二:每页记录数\r\n");
      out.write("\t* */\r\n");
      out.write("\tpageList(1,2);\r\n");
      out.write("\t//跳转到活动列表首页的时候,向后台发送查询所有市场活动信息列表的异步请求\r\n");
      out.write("\t//刷新页面的方法\r\n");
      out.write("\tfunction pageList(page,pageSize) {\r\n");
      out.write("\t\t$.ajax({\r\n");
      out.write("\t\t\turl : '/crm/workbench/clue/queryAllClue',\r\n");
      out.write("\t\t\tdata : {'page':page,\r\n");
      out.write("\t\t\t\t'pageSize':pageSize,\r\n");
      out.write("\t\t\t\t'fullname':$('#fullname').val(),\r\n");
      out.write("\t\t\t\t'company':$('#company').val(),\r\n");
      out.write("\t\t\t\t'phone':$('#phone').val(),\r\n");
      out.write("\t\t\t\t'mphone':$('#mphone').val(),\r\n");
      out.write("\t\t\t\t'source' : $('#source').val(),\r\n");
